import React, {useState} from 'react'
import OtpInput from 'react-otp-input'


export default function Otp () {

     const [otp, setOtp] = useState('')

     const handleOtp = (e) => {
         setOtp(e.target.value)
     }

    return (
        <div>
                            <div class=" h-64 py-3 rounded text-center">

            <h1 class='text-2xl font-bold'>OTP Verification</h1>
            <div class='flex flex-col mt-4'>
                {' '}
                <span>Enter the OTP you received at</span>{' '}
                <span class='font-bold'>+91 ******876</span>{' '}
            </div>
            <OtpInput
                value={otp}
                onChange={handleOtp}
                numInputs={6}
                containerStyle='flex flex-row justify-center text-center px-2 mt-5'
                inputStyle='m-2 border h-10 w-10 text-center form-control rounded'
            />{' '}
            <div class='flex justify-center text-center mt-5'>
                {' '}
                <a class='flex items-center text-blue-700 hover:text-blue-900 cursor-pointer'>
                    <span class='font-bold'>Resend OTP</span>
                    <i class='bx bx-caret-right ml-1'></i>
                </a>{' '}
            </div>
            </div>
        </div>
    )
}
